import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Loader2, Code2, Send, Eye } from "lucide-react";
import { ethers } from "ethers";
import { useToast } from "@/hooks/use-toast";

interface ContractInteractionProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  deploymentId: number;
  contractAddress: string;
  contractName: string;
}

interface AbiFunction {
  type: "function";
  name: string;
  inputs: Array<{
    name: string;
    type: string;
    internalType?: string;
  }>;
  outputs?: Array<{
    name: string;
    type: string;
  }>;
  stateMutability: "pure" | "view" | "nonpayable" | "payable";
}

interface CategorizedAbi {
  contractName: string;
  contractAddress: string;
  network: string;
  readFunctions: AbiFunction[];
  writeFunctions: AbiFunction[];
  events: any[];
}

export function ContractInteraction({
  open,
  onOpenChange,
  deploymentId,
  contractAddress,
  contractName,
}: ContractInteractionProps) {
  const { toast } = useToast();
  const [selectedFunction, setSelectedFunction] = useState<AbiFunction | null>(null);
  const [functionInputs, setFunctionInputs] = useState<Record<string, string>>({});
  const [functionResult, setFunctionResult] = useState<any>(null);
  const [isExecuting, setIsExecuting] = useState(false);
  const [ethValue, setEthValue] = useState("");

  const { data: abiData, isLoading } = useQuery<CategorizedAbi>({
    queryKey: [`/api/contracts/${deploymentId}/abi`],
    enabled: open,
  });

  useEffect(() => {
    if (!open) {
      setSelectedFunction(null);
      setFunctionInputs({});
      setFunctionResult(null);
      setEthValue("");
    }
  }, [open]);

  const handleFunctionSelect = (func: AbiFunction) => {
    setSelectedFunction(func);
    setFunctionInputs({});
    setFunctionResult(null);
    setEthValue("");
  };

  // Format result values, handling BigInt and ethers Result objects
  const formatResultValue = (value: any): any => {
    if (value === null || value === undefined) return value;

    // Handle BigInt
    if (typeof value === "bigint") {
      return value.toString();
    }

    // Handle arrays
    if (Array.isArray(value)) {
      return value.map(formatResultValue);
    }

    // Handle ethers.js Result objects (which are array-like with named properties)
    if (value && typeof value === "object" && value.constructor && value.constructor.name === "Result") {
      const formatted: any = {};
      Object.keys(value).forEach(key => {
        // Skip numeric keys (array indices) to avoid duplication
        if (!isNaN(Number(key))) return;
        formatted[key] = formatResultValue(value[key]);
      });
      // If there are named properties, return the object, otherwise return the array
      if (Object.keys(formatted).length > 0) {
        return formatted;
      }
      return value.map(formatResultValue);
    }

    // Handle plain objects
    if (value && typeof value === "object") {
      const formatted: any = {};
      Object.keys(value).forEach(key => {
        formatted[key] = formatResultValue(value[key]);
      });
      return formatted;
    }

    return value;
  };

  const handleInputChange = (paramKey: string, value: string) => {
    setFunctionInputs(prev => ({ ...prev, [paramKey]: value }));
  };

  const getParamKey = (input: any, index: number) => {
    return input.name || `param${index}`;
  };

  const parseInputValue = (value: string, type: string): any => {
    value = value.trim();

    if (!value) return undefined;

    // Check for complex nested types first (require JSON input)
    const isNested = (type.match(/\[/g) || []).length > 1; // Multiple brackets = nested
    const isTupleArray = type.startsWith("tuple[") || type === "tuple[]";

    if (isNested || isTupleArray) {
      // For nested arrays or tuple arrays, require JSON format
      try {
        return JSON.parse(value);
      } catch (error) {
        throw new Error(`Invalid JSON for ${type}. Expected format: [[1,2],[3,4]] or similar nested structure`);
      }
    }

    // Handle tuples/structs (expect JSON input)
    if (type === "tuple" || type.startsWith("tuple")) {
      try {
        return JSON.parse(value);
      } catch (error) {
        throw new Error(`Invalid JSON for tuple. Expected format: [value1, value2, ...]`);
      }
    }

    // Handle dynamic arrays (e.g., uint256[], address[]) - single-level only
    if (type.endsWith("[]")) {
      const baseType = type.slice(0, -2);
      return value.split(",").map(v => parseInputValue(v.trim(), baseType));
    }

    // Handle fixed-length arrays (e.g., uint256[3], address[2]) - single-level only
    const fixedArrayMatch = type.match(/^(.+)\[(\d+)\]$/);
    if (fixedArrayMatch) {
      const baseType = fixedArrayMatch[1];
      const expectedLength = parseInt(fixedArrayMatch[2]);
      const values = value.split(",").map(v => parseInputValue(v.trim(), baseType));
      if (values.length !== expectedLength) {
        throw new Error(`Expected ${expectedLength} values for ${type}, got ${values.length}`);
      }
      return values;
    }

    // Handle boolean
    if (type === "bool") {
      return value.toLowerCase() === "true" || value === "1";
    }

    // Handle integers (uint/int of any size)
    if (type.startsWith("uint") || type.startsWith("int")) {
      return value;
    }

    // Handle address
    if (type === "address") {
      return value;
    }

    // Handle bytes (bytes1-bytes32, bytes)
    if (type.startsWith("bytes")) {
      return value;
    }

    // Default: return as string
    return value;
  };

  const executeReadFunction = async () => {
    if (!selectedFunction) return;

    setIsExecuting(true);
    setFunctionResult(null);

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(
        contractAddress,
        [selectedFunction],
        provider
      );

      const args = selectedFunction.inputs.map((input, index) => {
        const paramKey = getParamKey(input, index);
        const value = functionInputs[paramKey] || "";
        return parseInputValue(value, input.type);
      }).filter(arg => arg !== undefined);

      const result = await contract[selectedFunction.name](...args);
      const formattedResult = formatResultValue(result);
      setFunctionResult(formattedResult);

      toast({
        title: "Function executed",
        description: `${selectedFunction.name}() returned successfully`,
      });
    } catch (error: any) {
      console.error("Error executing read function:", error);
      toast({
        variant: "destructive",
        title: "Execution failed",
        description: error.message || "Failed to execute function",
      });
    } finally {
      setIsExecuting(false);
    }
  };

  const executeWriteFunction = async () => {
    if (!selectedFunction) return;

    setIsExecuting(true);
    setFunctionResult(null);

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        contractAddress,
        [selectedFunction],
        signer
      );

      const args = selectedFunction.inputs.map((input, index) => {
        const paramKey = getParamKey(input, index);
        const value = functionInputs[paramKey] || "";
        return parseInputValue(value, input.type);
      }).filter(arg => arg !== undefined);

      const options: any = {};
      if (selectedFunction.stateMutability === "payable" && ethValue) {
        options.value = ethers.parseEther(ethValue);
      }

      const tx = await contract[selectedFunction.name](...args, options);

      toast({
        title: "Transaction sent",
        description: "Waiting for confirmation...",
      });

      const receipt = await tx.wait();

      setFunctionResult({
        transactionHash: receipt.hash,
        blockNumber: receipt.blockNumber.toString(),
        gasUsed: receipt.gasUsed.toString(),
        status: receipt.status === 1 ? "Success" : "Failed",
      });

      toast({
        title: "Transaction confirmed",
        description: `${selectedFunction.name}() executed successfully`,
      });
    } catch (error: any) {
      console.error("Error executing write function:", error);
      toast({
        variant: "destructive",
        title: "Transaction failed",
        description: error.message || "Failed to execute transaction",
      });
    } finally {
      setIsExecuting(false);
    }
  };

  const renderFunctionList = (functions: AbiFunction[], isReadOnly: boolean) => {
    if (functions.length === 0) {
      return (
        <div className="text-center py-8 text-muted-foreground">
          No {isReadOnly ? "read" : "write"} functions available
        </div>
      );
    }

    return (
      <div className="space-y-2">
        {functions.map((func, index) => (
          <Button
            key={index}
            variant={selectedFunction?.name === func.name ? "default" : "outline"}
            className="w-full justify-start"
            onClick={() => handleFunctionSelect(func)}
            data-testid={`button-function-${func.name}`}
          >
            <Code2 className="w-4 h-4 mr-2" />
            <span className="font-mono text-sm">{func.name}</span>
            {func.stateMutability === "payable" && (
              <Badge variant="secondary" className="ml-auto">
                payable
              </Badge>
            )}
          </Button>
        ))}
      </div>
    );
  };

  const renderFunctionInterface = () => {
    if (!selectedFunction) {
      return (
        <div className="flex items-center justify-center h-full text-muted-foreground">
          Select a function to interact with the contract
        </div>
      );
    }

    const isReadOnly = selectedFunction.stateMutability === "view" || selectedFunction.stateMutability === "pure";

    return (
      <Card>
        <CardHeader>
          <CardTitle className="font-mono text-lg">{selectedFunction.name}()</CardTitle>
          <div className="flex gap-2 flex-wrap">
            <Badge variant="outline">{selectedFunction.stateMutability}</Badge>
            {selectedFunction.stateMutability === "payable" && (
              <Badge variant="secondary">Accepts ETH</Badge>
            )}
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          {selectedFunction.inputs.length > 0 && (
            <div className="space-y-3">
              <Label className="text-sm font-semibold">Parameters</Label>
              {selectedFunction.inputs.map((input, index) => {
                const paramKey = getParamKey(input, index);
                return (
                  <div key={paramKey} className="space-y-1">
                    <Label htmlFor={`input-${paramKey}`} className="text-xs text-muted-foreground">
                      {input.name || `param${index}`} ({input.type})
                    </Label>
                    <Input
                      id={`input-${paramKey}`}
                      value={functionInputs[paramKey] || ""}
                      onChange={(e) => handleInputChange(paramKey, e.target.value)}
                      placeholder={
                        (() => {
                          const isNested = (input.type.match(/\[/g) || []).length > 1;
                          const isTupleArray = input.type.startsWith("tuple[") || input.type === "tuple[]";

                          if (isNested || isTupleArray) {
                            return "JSON: [[val1,val2],[val3,val4]] or nested structure";
                          }
                          if (input.type === "tuple" || input.type.startsWith("tuple")) {
                            return "JSON array: [value1, value2, ...]";
                          }
                          if (input.type.includes("[]") || /\[\d+\]/.test(input.type)) {
                            return "Comma-separated values";
                          }
                          if (input.type === "bool") {
                            return "true or false";
                          }
                          return `Enter ${input.type}`;
                        })()
                      }
                      data-testid={`input-param-${paramKey}`}
                    />
                  </div>
                );
              })}
            </div>
          )}

          {selectedFunction.stateMutability === "payable" && (
            <div className="space-y-1">
              <Label htmlFor="eth-value" className="text-xs text-muted-foreground">
                ETH Value (optional)
              </Label>
              <Input
                id="eth-value"
                value={ethValue}
                onChange={(e) => setEthValue(e.target.value)}
                placeholder="0.0"
                type="number"
                step="0.001"
                data-testid="input-eth-value"
              />
            </div>
          )}

          <Button
            onClick={isReadOnly ? executeReadFunction : executeWriteFunction}
            disabled={isExecuting}
            className="w-full"
            data-testid="button-execute-function"
          >
            {isExecuting ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Executing...
              </>
            ) : isReadOnly ? (
              <>
                <Eye className="w-4 h-4 mr-2" />
                Read
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Send Transaction
              </>
            )}
          </Button>

          {functionResult !== null && (
            <div className="space-y-1">
              <Label className="text-sm font-semibold">Result</Label>
              <Card className="bg-muted">
                <CardContent className="p-4">
                  <pre className="text-xs font-mono whitespace-pre-wrap break-all" data-testid="text-function-result">
                    {typeof functionResult === "object"
                      ? JSON.stringify(functionResult, null, 2)
                      : String(functionResult)}
                  </pre>
                </CardContent>
              </Card>
            </div>
          )}
        </CardContent>
      </Card>
    );
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-5xl max-h-[90vh]">
        <DialogHeader>
          <DialogTitle>{contractName}</DialogTitle>
          <DialogDescription>
            <code className="text-xs">{contractAddress}</code>
          </DialogDescription>
        </DialogHeader>

        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="w-8 h-8 animate-spin text-muted-foreground" />
          </div>
        ) : abiData ? (
          <Tabs defaultValue="read" className="flex-1">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="read" data-testid="tab-read-functions">
                Read Functions ({abiData.readFunctions.length})
              </TabsTrigger>
              <TabsTrigger value="write" data-testid="tab-write-functions">
                Write Functions ({abiData.writeFunctions.length})
              </TabsTrigger>
            </TabsList>

            <TabsContent value="read" className="flex-1 mt-4">
              <div className="grid grid-cols-3 gap-4 h-[500px]">
                <ScrollArea className="col-span-1 border rounded-lg p-4">
                  {renderFunctionList(abiData.readFunctions, true)}
                </ScrollArea>
                <div className="col-span-2">
                  {renderFunctionInterface()}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="write" className="flex-1 mt-4">
              <div className="grid grid-cols-3 gap-4 h-[500px]">
                <ScrollArea className="col-span-1 border rounded-lg p-4">
                  {renderFunctionList(abiData.writeFunctions, false)}
                </ScrollArea>
                <div className="col-span-2">
                  {renderFunctionInterface()}
                </div>
              </div>
            </TabsContent>
          </Tabs>
        ) : (
          <div className="text-center py-12 text-muted-foreground">
            Failed to load contract ABI
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

server/seed-templates.ts.content:
import { db } from "./db";
import { contractTemplates } from "@shared/schema";
import { CONTRACT_TEMPLATES } from "./templates/templates";
import { sql, and, eq } from "drizzle-orm";

export async function seedTemplates() {
  console.log("Seeding contract templates...");

  try {
    let newCount = 0;
    let existingCount = 0;

    for (const template of CONTRACT_TEMPLATES) {
      // Check if template already exists (name + solcVersion as unique key)
      const existing = await db
        .select()
        .from(contractTemplates)
        .where(
          and(
            eq(contractTemplates.name, template.name),
            eq(contractTemplates.solcVersion, template.solcVersion)
          )
        )
        .limit(1);

      if (existing.length === 0) {
        // Insert new template
        await db.insert(contractTemplates).values(template);
        newCount++;
      } else {
        existingCount++;
      }
    }

    const totalCount = await db.select({ count: sql<number>`count(*)` }).from(contractTemplates);
    console.log(` Templates seeded successfully. Added: ${newCount}, Existing: ${existingCount}, Total: ${totalCount[0].count}`);
  } catch (error) {
    console.error("Error seeding templates:", error);
    // Don't throw - allow server to start even if seeding fails
  }
}

client/src/components/ThemeProvider.tsx.content:
import { createContext, useContext, useEffect, useState } from "react";

type Theme = "light" | "dark";

interface ThemeContextType {
  theme: Theme;
  setTheme: (theme: Theme) => void;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [theme, setTheme] = useState<Theme>(() => {
    const stored = localStorage.getItem("theme") as Theme | null;
    if (stored) return stored;
    return window.matchMedia("(prefers-color-scheme: dark)").matches ? "dark" : "light";
  });

  useEffect(() => {
    const root = document.documentElement;
    root.classList.remove("light", "dark");
    root.classList.add(theme);
    localStorage.setItem("theme", theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  return (
    <ThemeContext.Provider value={{ theme, setTheme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error("useTheme must be used within ThemeProvider");
  }
  return context;
}

server/gas-estimator.ts.content:
import { JsonRpcProvider, formatUnits, AbiCoder, Interface } from "ethers";
import type { GasEstimateRequest, GasEstimateResponse, Network } from "@shared/schema";
import { NETWORKS } from "@shared/schema";

// Provider cache keyed by chainId
const providerCache = new Map<number, JsonRpcProvider>();

// Token price cache with timestamps
interface TokenPriceCache {
  price: number;
  timestamp: number;
}

const tokenPriceCache = new Map<string, TokenPriceCache>();
const PRICE_CACHE_DURATION = 10 * 60 * 1000; // 10 minutes in milliseconds

// CoinGecko token ID mapping
const COINGECKO_TOKEN_IDS: Record<number, string> = {
  1: "ethereum",           // Ethereum Mainnet
  11155111: "ethereum",    // Sepolia
  56: "binancecoin",       // BSC
  97: "binancecoin",       // BSC Testnet
  137: "matic-network",    // Polygon
  80002: "matic-network",  // Polygon Amoy
  42161: "ethereum",       // Arbitrum
  421614: "ethereum",      // Arbitrum Sepolia
  10: "ethereum",          // Optimism
  11155420: "ethereum",    // Optimism Sepolia
  43114: "avalanche-2",    // Avalanche
  43113: "avalanche-2",    // Avalanche Fuji
};

function getOrCreateProvider(chainId: number): JsonRpcProvider {
  if (providerCache.has(chainId)) {
    return providerCache.get(chainId)!;
  }

  const network = NETWORKS.find((n) => n.chainId === chainId);
  if (!network) {
    throw new Error(`Unsupported network with chainId ${chainId}`);
  }

  const provider = new JsonRpcProvider(network.rpcUrl, chainId);
  providerCache.set(chainId, provider);
  return provider;
}

async function getTokenPriceUSD(chainId: number): Promise<number | null> {
  const tokenId = COINGECKO_TOKEN_IDS[chainId];
  if (!tokenId) {
    return null; // No mapping available
  }

  // Check cache
  const cached = tokenPriceCache.get(tokenId);
  if (cached && Date.now() - cached.timestamp < PRICE_CACHE_DURATION) {
    return cached.price;
  }

  // Fetch fresh price from CoinGecko
  try {
    const response = await fetch(
      `https://api.coingecko.com/api/v3/simple/price?ids=${tokenId}&vs_currencies=usd`,
      { signal: AbortSignal.timeout(5000) } // 5 second timeout
    );

    if (!response.ok) {
      console.warn(`CoinGecko API failed: ${response.status}`);
      return null;
    }

    const data = await response.json();
    const price = data[tokenId]?.usd;

    if (typeof price !== "number") {
      console.warn(`Invalid price data from CoinGecko for ${tokenId}`);
      return null;
    }

    // Update cache
    tokenPriceCache.set(tokenId, { price, timestamp: Date.now() });
    return price;
  } catch (error) {
    console.warn("Failed to fetch token price from CoinGecko:", error);
    return null;
  }
}

function encodeConstructorArgs(abi: any[], constructorArgs: string[]): string {
  // Find constructor in ABI
  const constructorAbi = abi.find((item) => item.type === "constructor");

  if (!constructorAbi || constructorArgs.length === 0) {
    return "0x"; // No constructor or no args
  }

  try {
    const iface = new Interface(abi);
    const encoded = iface.encodeDeploy(constructorArgs);
    return encoded;
  } catch (error) {
    throw new Error(`Failed to encode constructor arguments: ${error instanceof Error ? error.message : String(error)}`);
  }
}

export async function estimateDeploymentGas(
  request: GasEstimateRequest
): Promise<GasEstimateResponse> {
  try {
    const { bytecode, constructorArgs, abi, chainId } = request;

    // Get network info
    const network = NETWORKS.find((n) => n.chainId === chainId);
    if (!network) {
      return {
        success: false,
        error: `Unsupported network with chainId ${chainId}`,
      };
    }

    // Get or create provider
    const provider = getOrCreateProvider(chainId);

    // Encode constructor arguments
    const encodedArgs = encodeConstructorArgs(abi, constructorArgs);
    const deploymentData = bytecode + encodedArgs.slice(2); // Remove '0x' from encoded args

    // Estimate gas units
    let gasUnits: bigint;
    try {
      gasUnits = await provider.estimateGas({
        data: deploymentData,
      });
    } catch (error) {
      return {
        success: false,
        error: `Gas estimation failed: ${error instanceof Error ? error.message : String(error)}`,
      };
    }

    // Get fee data from provider
    let feeData;
    try {
      feeData = await provider.getFeeData();
    } catch (error) {
      return {
        success: false,
        error: `Failed to fetch gas prices: ${error instanceof Error ? error.message : String(error)}`,
      };
    }

    // Check if network supports EIP-1559
    const supportsEIP1559 = feeData.maxFeePerGas !== null && feeData.maxPriorityFeePerGas !== null;

    let slowGasPrice: bigint;
    let standardGasPrice: bigint;
    let fastGasPrice: bigint;
    let eip1559Data: any = undefined;

    if (supportsEIP1559) {
      // EIP-1559 network - use maxFeePerGas and maxPriorityFeePerGas
      const baseFee = feeData.maxFeePerGas! - feeData.maxPriorityFeePerGas!;
      const standardPriorityFee = feeData.maxPriorityFeePerGas!;

      // Calculate priority fee tiers based on percentiles
      // Slow: 60% of standard (1-10th percentile equivalent)
      // Standard: 100% (50-60th percentile equivalent)
      // Fast: 200% (90th+ percentile equivalent)
      const slowPriorityFee = (standardPriorityFee * BigInt(60)) / BigInt(100);
      const fastPriorityFee = (standardPriorityFee * BigInt(200)) / BigInt(100);

      // Calculate maxFeePerGas using the standard formula: 2  baseFee + priorityFee
      // This ensures transaction validity for 6 consecutive full blocks (12.5% increase per block)
      const slowMaxFee = (baseFee * BigInt(2)) + slowPriorityFee;
      const standardMaxFee = (baseFee * BigInt(2)) + standardPriorityFee;
      const fastMaxFee = (baseFee * BigInt(2)) + fastPriorityFee;

      // For cost calculation, use the actual maxFeePerGas (worst case)
      slowGasPrice = slowMaxFee;
      standardGasPrice = standardMaxFee;
      fastGasPrice = fastMaxFee;

      // Store EIP-1559 specific data
      eip1559Data = {
        baseFee: baseFee.toString(),
        slow: {
          maxFeePerGas: slowMaxFee.toString(),
          maxPriorityFeePerGas: slowPriorityFee.toString(),
        },
        standard: {
          maxFeePerGas: standardMaxFee.toString(),
          maxPriorityFeePerGas: standardPriorityFee.toString(),
        },
        fast: {
          maxFeePerGas: fastMaxFee.toString(),
          maxPriorityFeePerGas: fastPriorityFee.toString(),
        },
      };
    } else {
      // Legacy network - use gasPrice with tiers (80%, 100%, 120%)
      const baseGasPrice = feeData.gasPrice || BigInt(0);
      if (baseGasPrice === BigInt(0)) {
        return {
          success: false,
          error: "Unable to determine gas price from network",
        };
      }

      slowGasPrice = (baseGasPrice * BigInt(80)) / BigInt(100);
      standardGasPrice = baseGasPrice;
      fastGasPrice = (baseGasPrice * BigInt(120)) / BigInt(100);
    }

    // Calculate costs in native token
    const slowCost = (gasUnits * slowGasPrice);
    const standardCost = (gasUnits * standardGasPrice);
    const fastCost = (gasUnits * fastGasPrice);

    // Fetch token price in USD (optional, graceful failure)
    const tokenPriceUSD = await getTokenPriceUSD(chainId);

    // Helper to format native token amount
    const formatNative = (wei: bigint): string => {
      return formatUnits(wei, network.nativeCurrency.decimals);
    };

    // Helper to calculate USD cost
    const calculateUSD = (wei: bigint): string | undefined => {
      if (!tokenPriceUSD) return undefined;
      const nativeAmount = parseFloat(formatNative(wei));
      return (nativeAmount * tokenPriceUSD).toFixed(2);
    };

    return {
      success: true,
      gasEstimate: {
        gasUnits: gasUnits.toString(),
        gasPrices: {
          slow: slowGasPrice.toString(),
          standard: standardGasPrice.toString(),
          fast: fastGasPrice.toString(),
        },
        eip1559: eip1559Data,
        costs: {
          slow: {
            native: formatNative(slowCost),
            usd: calculateUSD(slowCost),
          },
          standard: {
            native: formatNative(standardCost),
            usd: calculateUSD(standardCost),
          },
          fast: {
            native: formatNative(fastCost),
            usd: calculateUSD(fastCost),
          },
        },
        nativeCurrency: network.nativeCurrency.symbol,
        supportsEIP1559,
      },
    };
  } catch (error) {
    console.error("Gas estimation error:", error);
    return {
      success: false,
      error: `Unexpected error during gas estimation: ${error instanceof Error ? error.message : String(error)}`,
    };
  }
}

server/verifier.ts.content:
import { NETWORKS, type Network } from "@shared/schema";
import { config } from "../config";

interface VerificationPayload {
  contractAddress: string;
  sourceCode: string;
  contractName: string;
  compilerVersion: string;
  constructorArguments?: string;
  chainId: number;
  optimizationEnabled?: boolean;
  optimizationRuns?: number;
  evmVersion?: string;
}

export class ContractVerifier {
  private readonly ETHERSCAN_V2_BASE_URL = 'https://api.etherscan.io/v2/api';

  private getApiKey(): string | null {
    return config.ETHERSCAN_API_KEY;
  }

  async verifyContract(payload: VerificationPayload): Promise<{
    success: boolean;
    message: string;
    guid?: string;
  }> {
    const network = NETWORKS.find(n => n.chainId === payload.chainId);
    if (!network) {
      return {
        success: false,
        message: `Network not found for chain ID ${payload.chainId}`,
      };
    }

    const apiKey = this.getApiKey();
    if (!apiKey) {
      return {
        success: false,
        message: 'ETHERSCAN_API_KEY not configured. Please add it to environment variables.',
      };
    }

    try {
      // Compiler version should already include 'v' prefix (e.g., "v0.8.30+commit.6182c971")
      // If it doesn't have the prefix, add it
      const compilerVersion = payload.compilerVersion.startsWith('v') 
        ? payload.compilerVersion 
        : `v${payload.compilerVersion}`;

      // Use provided optimization settings or defaults
      const optimizationEnabled = payload.optimizationEnabled ?? true;
      const optimizationRuns = payload.optimizationRuns ?? 200;
      const evmVersion = payload.evmVersion ?? 'paris';

      const params = new URLSearchParams({
        chainid: payload.chainId.toString(),
        module: 'contract',
        action: 'verifysourcecode',
        contractaddress: payload.contractAddress,
        sourceCode: payload.sourceCode,
        codeformat: 'solidity-single-file',
        contractname: payload.contractName,
        compilerversion: compilerVersion,
        optimizationUsed: optimizationEnabled ? '1' : '0',
        runs: optimizationRuns.toString(),
        evmversion: evmVersion,
        apikey: apiKey,
      });

      if (payload.constructorArguments) {
        params.append('constructorArguments', payload.constructorArguments);
      }

      // Log request for debugging
      console.log('[Etherscan V2 API] Verification request:', {
        chainId: payload.chainId,
        contractAddress: payload.contractAddress,
        contractName: payload.contractName,
        compilerVersion: compilerVersion,
        optimizationEnabled,
        optimizationRuns,
        evmVersion,
        hasConstructorArgs: !!payload.constructorArguments,
      });

      // Etherscan V2 API requires chainid in both URL and POST body
      const url = `${this.ETHERSCAN_V2_BASE_URL}?chainid=${payload.chainId}`;

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: params.toString(),
      });

      if (!response.ok) {
        throw new Error(`HTTP error: ${response.status}`);
      }

      const data = await response.json();

      // Log full response for debugging
      console.log('[Etherscan V2 API] Verification response:', JSON.stringify(data, null, 2));

      if (data.status === '1') {
        return {
          success: true,
          message: 'Contract verification submitted successfully',
          guid: data.result,
        };
      } else {
        return {
          success: false,
          message: data.result || 'Verification failed',
        };
      }
    } catch (error) {
      console.error('Verification error:', error);
      return {
        success: false,
        message: error instanceof Error ? error.message : 'Unknown error occurred',
      };
    }
  }

  async checkVerificationStatus(guid: string, chainId: number): Promise<{
    success: boolean;
    status: string;
  }> {
    const network = NETWORKS.find(n => n.chainId === chainId);
    if (!network) {
      return {
        success: false,
        status: 'Network not found',
      };
    }

    const apiKey = this.getApiKey();
    if (!apiKey) {
      return {
        success: false,
        status: 'API key not configured',
      };
    }

    try {
      const params = new URLSearchParams({
        module: 'contract',
        action: 'checkverifystatus',
        guid,
        apikey: apiKey,
      });

      // For status checks, chainid must be in URL query string (not in params to avoid duplication)
      const url = `${this.ETHERSCAN_V2_BASE_URL}?chainid=${chainId}&${params.toString()}`;

      console.log('[Etherscan V2 API] Status check request:', { chainId, guid });

      const response = await fetch(url);
      const data = await response.json();

      console.log('[Etherscan V2 API] Status check response:', JSON.stringify(data, null, 2));

      return {
        success: data.status === '1',
        status: data.result || 'Unknown status',
      };
    } catch (error) {
      console.error('Status check error:', error);
      return {
        success: false,
        status: 'Failed to check status',
      };
    }
  }
}

export const contractVerifier = new ContractVerifier();

