import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import type { ContractTemplate } from "@shared/schema";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { FileCode, Sparkles, Search } from "lucide-react";

interface TemplateListProps {
  onSelectTemplate: (template: ContractTemplate) => void;
}

const CATEGORIES = [
  { value: "all", label: "All" },
  { value: "Tokens", label: "Tokens" },
  { value: "NFT", label: "NFT" },
  { value: "DeFi", label: "DeFi" },
  { value: "Governance", label: "Gov" },
  { value: "Security", label: "Security" },
  { value: "Utilities", label: "Utils" },
];

export function TemplateList({ onSelectTemplate }: TemplateListProps) {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");

  const { data: templates, isLoading } = useQuery<ContractTemplate[]>({
    queryKey: ["/api/templates"],
  });

  const filteredTemplates = templates?.filter((t) => {
    const matchesCategory = selectedCategory === "all" || t.category === selectedCategory;

    if (!searchQuery) return matchesCategory;

    const query = searchQuery.toLowerCase();
    const matchesSearch = 
      t.name.toLowerCase().includes(query) ||
      t.description.toLowerCase().includes(query) ||
      (t.tags as string[]).some((tag) => tag.toLowerCase().includes(query));

    return matchesCategory && matchesSearch;
  });

  return (
    <div className="flex flex-col h-full gap-4 p-4">
      <div>
        <h2 className="text-lg font-semibold flex items-center gap-2 mb-2">
          <FileCode className="w-4 h-4" />
          Contract Templates
        </h2>
        <p className="text-sm text-muted-foreground mb-3">
          Browse and use production-ready Solidity templates
        </p>

        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search templates..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-templates"
          />
        </div>
      </div>

      <Tabs value={selectedCategory} onValueChange={setSelectedCategory} className="flex-1 flex flex-col min-h-0">
        <TabsList className="grid w-full grid-cols-7" data-testid="tabs-category">
          {CATEGORIES.map((cat) => (
            <TabsTrigger
              key={cat.value}
              value={cat.value}
              data-testid={`tab-${cat.value}`}
              className="text-xs px-2"
            >
              {cat.label}
            </TabsTrigger>
          ))}
        </TabsList>

        {CATEGORIES.map((cat) => (
          <TabsContent key={cat.value} value={cat.value} className="flex-1 min-h-0 mt-3">
            <ScrollArea className="h-full">
              {isLoading ? (
                <div className="text-center py-8 text-muted-foreground text-sm">
                  Loading templates...
                </div>
              ) : filteredTemplates && filteredTemplates.length > 0 ? (
                <div className="space-y-3 pr-3">
                  {filteredTemplates.map((template) => (
                    <Card key={template.id} className="hover-elevate" data-testid={`card-template-${template.id}`}>
                      <CardHeader className="p-4 space-y-1">
                        <div className="flex items-start justify-between gap-2">
                          <CardTitle className="text-sm font-medium">
                            {template.name}
                            {template.featured && (
                              <Sparkles className="inline w-3 h-3 ml-1 text-primary" />
                            )}
                          </CardTitle>
                          <Badge variant="outline" className="shrink-0 text-xs">
                            {template.category}
                          </Badge>
                        </div>
                        <CardDescription className="text-xs line-clamp-2">
                          {template.description}
                        </CardDescription>
                      </CardHeader>

                      <CardContent className="p-4 pt-0">
                        <div className="flex flex-wrap gap-1">
                          {(template.tags as string[]).slice(0, 3).map((tag, idx) => (
                            <Badge key={idx} variant="secondary" className="text-xs">
                              {tag}
                            </Badge>
                          ))}
                          {(template.tags as string[]).length > 3 && (
                            <Badge variant="secondary" className="text-xs">
                              +{(template.tags as string[]).length - 3}
                            </Badge>
                          )}
                        </div>
                      </CardContent>

                      <CardFooter className="p-4 pt-0 flex items-center justify-between gap-2">
                        <span className="text-xs text-muted-foreground">
                          v{template.solcVersion}
                        </span>
                        <Button
                          size="sm"
                          onClick={() => onSelectTemplate(template)}
                          data-testid={`button-use-${template.id}`}
                          className="h-7 text-xs"
                        >
                          Use Template
                        </Button>
                      </CardFooter>
                    </Card>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 text-muted-foreground text-sm">
                  No templates found
                </div>
              )}
            </ScrollArea>
          </TabsContent>
        ))}
      </Tabs>
    </div>
  );
}

client/src/components/EditorPanel.tsx.content:
import { useState } from "react";
import Editor from "@monaco-editor/react";
import { Card, CardHeader, CardContent, CardFooter } from "@/components/ui/card";
import { FileTabs } from "./FileTabs";
import { Button } from "@/components/ui/button";
import { Download, Upload, FileCode2 } from "lucide-react";
import type { WorkspaceFile } from "@shared/schema";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface EditorPanelProps {
  code: string;
  onCodeChange: (code: string) => void;
  solcVersion: string;
  onSolcVersionChange: (version: string) => void;
  activeFile: WorkspaceFile | null;
  openFiles: WorkspaceFile[];
  onTabClose: (file: WorkspaceFile) => void;
  onFileSelect: (file: WorkspaceFile) => void;
  onOpenTemplates?: () => void;
  isLeftPanelCollapsed?: boolean;
}

const SOLIDITY_VERSIONS = [
  "0.8.30",
  "0.8.29",
  "0.8.28",
  "0.8.27",
  "0.8.26",
  "0.8.25",
  "0.8.24",
  "0.8.23",
  "0.8.22",
  "0.8.21",
  "0.8.20",
  "0.8.19",
  "0.8.18",
  "0.8.17",
];

export function EditorPanel({
  code,
  onCodeChange,
  solcVersion,
  onSolcVersionChange,
  activeFile,
  openFiles,
  onTabClose,
  onFileSelect,
  onOpenTemplates,
  isLeftPanelCollapsed = false,
}: EditorPanelProps) {
  const [charCount, setCharCount] = useState(code.length);

  const handleEditorChange = (value: string | undefined) => {
    const newValue = value || "";
    onCodeChange(newValue);
    setCharCount(newValue.length);
  };

  const handleDownload = () => {
    const blob = new Blob([code], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "contract.sol";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target?.result as string;
        onCodeChange(content);
      };
      reader.readAsText(file);
    }
  };

  return (
    <Card className="h-full flex flex-col rounded-none border-0" data-testid="editor-panel">
      <CardHeader className={`p-3 border-b gap-3 ${isLeftPanelCollapsed ? 'pl-14' : ''}`}>
        <div className="flex flex-wrap items-center justify-between gap-3">
          <div className="flex items-center gap-2">
            <FileCode2 className="w-4 h-4 text-primary" />
            <span className="text-sm font-medium">Code Editor</span>
            {activeFile && (
              <span className="text-xs text-muted-foreground">
                 {activeFile.path}
              </span>
            )}
          </div>
          <div className="flex items-center gap-2">
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button 
                  variant="outline" 
                  size="sm" 
                  data-testid="dropdown-solc-version"
                  className="font-mono text-xs"
                >
                  v{solcVersion}
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="max-h-[300px] overflow-y-auto">
                {SOLIDITY_VERSIONS.map((version) => (
                  <DropdownMenuItem
                    key={version}
                    onClick={() => onSolcVersionChange(version)}
                    data-testid={`menu-item-version-${version}`}
                  >
                    <span className="font-mono text-xs">v{version}</span>
                  </DropdownMenuItem>
                ))}
              </DropdownMenuContent>
            </DropdownMenu>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={handleDownload} 
              data-testid="button-download"
            >
              <Download className="w-4 h-4" />
            </Button>
            <label htmlFor="file-upload">
              <Button
                variant="outline"
                size="sm"
                onClick={() => document.getElementById("file-upload")?.click()}
                data-testid="button-upload"
              >
                <Upload className="w-4 h-4" />
              </Button>
            </label>
            <input
              id="file-upload"
              type="file"
              accept=".sol"
              className="hidden"
              onChange={handleUpload}
              data-testid="input-file-upload"
            />
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-0 flex flex-col flex-1 overflow-hidden">
        {openFiles.length > 0 && activeFile && (
          <div data-testid="file-tabs">
            <FileTabs
              openFiles={openFiles}
              activeFile={activeFile}
              onFileSelect={onFileSelect}
              onFileClose={onTabClose}
            />
          </div>
        )}

        <div className="flex-1 overflow-hidden">
          <Editor
            height="100%"
            language="sol"
            value={code}
            onChange={handleEditorChange}
            theme="vs-dark"
            options={{
              minimap: { enabled: true },
              fontSize: 14,
              lineNumbers: "on",
              rulers: [80],
              wordWrap: "on",
              automaticLayout: true,
              scrollBeyondLastLine: false,
              padding: { top: 10 },
            }}
          />
        </div>
      </CardContent>

      <CardFooter className={`p-2 border-t text-xs text-muted-foreground justify-between gap-3 ${isLeftPanelCollapsed ? 'pl-14' : ''}`}>
        <div>{charCount} characters</div>
        {onOpenTemplates && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onOpenTemplates}
            data-testid="button-open-templates"
            className="h-7 text-xs"
          >
            Browse Templates
          </Button>
        )}
      </CardFooter>
    </Card>
  );
}

