#!/usr/bin/env node

/**
 * Environment Variable Validation Script
 * Ensures all required environment variables are set before starting the application
 */

import { config } from 'dotenv';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Load .env file from project root
config({ path: join(__dirname, '..', '.env') });

const colors = {
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  reset: '\x1b[0m'
};

console.log('==========================================');
console.log('Environment Variable Validation');
console.log('==========================================\n');

// Required backend variables
const requiredBackend = [
  'DATABASE_URL',
  'SESSION_SECRET'
];

// Optional backend variables
const optionalBackend = [
  'ETHERSCAN_API_KEY',
  'BSCSCAN_API_KEY',
  'POLYGONSCAN_API_KEY'
];

// Frontend variables (VITE_*)
const frontendVars = [
  'VITE_REOWN_PROJECT_ID'
];

let hasErrors = false;
let hasWarnings = false;

// Check required backend variables
console.log('Backend Variables (Required):');
requiredBackend.forEach(varName => {
  const value = process.env[varName];
  if (!value) {
    console.log(`${colors.red} ${varName} - MISSING${colors.reset}`);
    hasErrors = true;
  } else {
    // Mask sensitive values
    const maskedValue = varName.includes('SECRET') || varName.includes('PASSWORD')
      ? value.substring(0, 10) + '...'
      : varName.includes('DATABASE_URL')
      ? value.replace(/:[^@]*@/, ':***@')
      : 'set';
    console.log(`${colors.green} ${varName} - ${maskedValue}${colors.reset}`);
  }
});

// Check optional backend variables
console.log('\nBackend Variables (Optional):');
optionalBackend.forEach(varName => {
  const value = process.env[varName];
  if (!value) {
    console.log(`${colors.yellow} ${varName} - not set (some features may be limited)${colors.reset}`);
    hasWarnings = true;
  } else {
    console.log(`${colors.green} ${varName} - set${colors.reset}`);
  }
});

// Check frontend variables
console.log('\nFrontend Variables:');
frontendVars.forEach(varName => {
  const value = process.env[varName];
  if (!value) {
    console.log(`${colors.yellow} ${varName} - not set (wallet connection will be disabled)${colors.reset}`);
    hasWarnings = true;
  } else {
    console.log(`${colors.green} ${varName} - ${value}${colors.reset}`);
  }
});

// Summary
console.log('\n==========================================');
if (hasErrors) {
  console.log(`${colors.red} Validation Failed${colors.reset}`);
  console.log('\nMissing required environment variables.');
  console.log('Please create a .env file with the required values.\n');
  console.log('See .env.template for reference.\n');
  process.exit(1);
} else if (hasWarnings) {
  console.log(`${colors.yellow} Validation Passed with Warnings${colors.reset}`);
  console.log('\nSome optional features may be disabled.\n');
  process.exit(0);
} else {
  console.log(`${colors.green} All Variables Set${colors.reset}\n`);
  process.exit(0);
}

config.ts.content:
/**
 * Server-Side Configuration
 * 
 * Configuration Loading Strategy (Layered Approach):
 * 1. Replit Secrets / OS Environment Variables (highest priority)
 * 2. .env file (fallback when OS env vars not set)
 * 
 * This means:
 * - In Replit: Secrets are used (recommended approach)
 * - In local/VPS: .env file is used as fallback
 * - OS environment variables ALWAYS override .env file values
 * 
 * For VPS deployment: Copy the .env.example file to .env and update all values
 * with your production credentials. The .env file will be read automatically.
 * 
 * IMPORTANT: This file is for SERVER-SIDE configuration only.
 * For client-side configuration, use client/src/config.ts
 */

// Load .env file with override:false so OS environment variables take precedence
import dotenv from "dotenv";
dotenv.config({ override: false });

// Helper to validate required environment variables
function getRequiredEnv(key: string): string {
  const value = process.env[key];
  if (!value) {
    throw new Error(
      `${key} is not set. Please configure it in your .env file or environment variables.`
    );
  }
  return value;
}

// Helper to get optional environment variable with default
function getOptionalEnv(key: string, defaultValue: string): string {
  return process.env[key] || defaultValue;
}

// Helper to determine if running in development
const isDevelopment = process.env.NODE_ENV !== "production";

export const config = {
  /**
   * Database Configuration
   * Required: PostgreSQL connection URL
   * 
   * This will throw a clear error if not set, guiding users to configure it
   */
  get DATABASE_URL(): string {
    return getRequiredEnv("DATABASE_URL");
  },

  /**
   * Etherscan API Key
   * Optional: Required for contract verification
   * Get from: https://etherscan.io/myapikey
   */
  get ETHERSCAN_API_KEY(): string {
    return getOptionalEnv("ETHERSCAN_API_KEY", "");
  },

  /**
   * Reown (WalletConnect) Project ID
   * Optional: Used for server-side wallet operations if needed
   * Get from: https://cloud.reown.com
   * 
   * Note: Client-side uses client/src/config.ts for REOWN_PROJECT_ID
   */
  get REOWN_PROJECT_ID(): string {
    return getOptionalEnv("REOWN_PROJECT_ID", "");
  },

  /**
   * Session Secret
   * Used for session encryption
   * 
   * Development: Uses a default value
   * Production: Requires explicit configuration
   */
  get SESSION_SECRET(): string {
    if (!isDevelopment && !process.env.SESSION_SECRET) {
      throw new Error(
        "SESSION_SECRET must be set in production. Generate with: node -e \"console.log(require('crypto').randomBytes(32).toString('hex'))\""
      );
    }
    return getOptionalEnv("SESSION_SECRET", "dev-secret-key-change-in-production");
  },

  /**
   * Server Port
   */
  get PORT(): number {
    return parseInt(getOptionalEnv("PORT", "5000"), 10);
  },

  /**
   * Node Environment
   */
  get NODE_ENV(): "development" | "production" {
    return (process.env.NODE_ENV as "development" | "production") || "development";
  },
} as const;
